let slideIndex = 1
moveSlides(slideIndex);

export function changeImageIndex(n) {
  moveSlides(slideIndex += n)
}

function moveSlides(n) {
  let k;
  let slides = document.querySelector("mySlides");
  if (n > 5) {slideIndex = 1}
  if (n < 1) {slideIndex = 5}

  for (k = 0; k < 5; k++) {
    slides[k].style.display = "none";
  }
  slides[slideIndex -1].style.display = "block";
}
