  DetailsContainer,
  ProductImages,
  ProductImage,
  ProductInfo,
} from "../../styles/productDetails";
import { client } from "../../App";
import { gql, useQuery } from "@apollo/client";
import { useParams } from "react-router";
import { ProductTypes } from "../../types";
import { selectedProduct } from "../../redux/actions/actions";

interface Product {
  id: string;
  name: string;
  inStock: boolean;
  gallery: [];
  description: string;
  category: string;
  brand: string;
  attributes: [];
  prices: [];
}

const initialProduct: Product = {
  id: "string",
  name: "string",
  inStock: false,
  gallery: [],
  description: "string",
  category: "string",
  brand: "string",
  attributes: [],
  prices: [],
};

const Get_Product = () =>
  client.query({
    query: gql`
      query product($id: String!) {
        product(id: $id) {
          id
          name
          inStock
          gallery
          description
          category
          brand
          attributes {
            id
            name
            type
            items {
              displayValue
              value
              id
            }
          }
          prices {
            currency {
              label
              symbol
            }
            amount
          }
        }
      }
    `,
    variables: { id: "ps-5" },
  });

class ProductDetails extends React.Component<
  { params: { id: string }; query: { data: any } },
  { product: Product }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      product: { ...initialProduct },
    };
  }
  componentDidMount() {
    Get_Product().then((res) => {
      console.log("Data", res.data.product);
      this.setState({
        product: res.data.product,
      });
    });
