let slideIndex = 1
let galleryLength = 0
moveSlides(slideIndex,galleryLength);

export function changeImageIndex(n:number, l:number) {
  moveSlides(slideIndex += n,galleryLength = l)
}

function moveSlides(n:number,l:any) {
  let k;
  let slides = l;
  if (n > slides) {slideIndex = 1}
  if (n < 1) {slideIndex = slides}

  for (k = 0; k < slides; k++) {
    slides[k].style.display = "none";
  }
  slides[slideIndex -1].style.display = "block";
}
