import { addToCart, getTotals } from "../../redux/cartSlice";

export function mapStateToProps(state: any) {
  return {
    currency: state.selection["selectedCurrency"],
  };
}
export function mapDispatchToProps(dispatch: any) {
  return {
    addToCart: (product: any) => dispatch(addToCart(product)),
    getTotals: () => dispatch(getTotals()),
  };
}

export const addProductToCart = (product: {}, attributes) => {
  let attribute = attributes;
  addToCart({ product, attribute });
  getTotals();
  predefinedAttributes(product, attribute );
}

export const setAttributes = (itm: { id: string }, attr: string, attributed) =>{
  let attribute = attributed;
  let a = { [attr]: itm.id };
  let ab = Object.assign(attribute, a);
  attributed =  ({ attributes: ab });
}

export const predefinedAttributes = (product, attributed) => {
  const attributeId = product.attributes.map((attr) => attr.id);
  const itemAttributes = product.attributes.map((attr) => attr);
  const attribute = itemAttributes.map((itm) => itm.items[0].id);
  let ab = {};
  attributeId.forEach((value1, index) => {
    const value2 = attribute[index];
    let a = { [value1]: value2 };
    ab = Object.assign(ab, a);
  });
  (attributed = ab) 
  // attributed = ({ attributes: ab });
}
