import { createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";


const initialState = {
  cartItems: localStorage.getItem("cartItems")
    ? JSON.parse(localStorage.getItem("cartItems")!)
    : [],
  cartTotalQuantity: 0,
  cartTotalAmount: 0,
};

const objCompare = (attributes:any, attribute:any) => {
  if (JSON.stringify(attributes) === JSON.stringify(attribute)) {
    return true;
  }
  if (JSON.stringify(attributes) !== JSON.stringify(attribute) ){
    return false;
  }
  return true;
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addToCart: (state, {payload,type}) => {
      console.log(payload)
      const attributes = (payload?.attr?.map((attr:any) => attr.itm.id) )
