import React from "react";
import { connect } from "react-redux";
import { setCategoryTitle, setCurrencyType } from "../../redux/actions/actions";
import { TopNavTypes } from "../../types";

class Cart extends React.Component<
  {
    setCategoryTitle: any;
    setCurrencyType: any;
    cart: { cartItems: [] };
  },
  TopNavTypes
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [localStorage.getItem("cartItems")],
      categories: [],
      category: "all",
      currencies: [],
      currency: "USD",
    };
  }
  componentDidMount() {
    console.log(this.state.products);
  }

  render() {
    return (
      <>
        <div
          style={{ width: "88%", margin: "10px auto", backgroundColor: "#fff" }}
        >
          <h3>CART</h3>
          <br />
          <hr />

          {this.props.cart.cartItems?.length >= 0 ? (
            this.props.cart.cartItems.map((item: { name: string }) => (
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div>
                  <h4>{item.name}</h4>
                  <h6>Running Short</h6>
                  <h6>$50.00</h6>
                  <div>
                    <button>S</button>
                  </div>
                </div>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    gap: "10px",
                  }}
                >
                  <div>
                    <button>+</button>
                    <h4 style={{ textAlign: "center" }}>1</h4>
                    <button>-</button>
                  </div>
                  <div>
                    <p>Subtotal</p>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div>No Items in Cart</div>
          )}
          <hr />
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginTop: "30px",
            }}
          >
            <div>
              <button>Clear Cart</button>
            </div>
            <div>
              <div>
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <span>Subtotal</span>
                  <span>200</span>
                </div>
                <p>Shipping and taxes calculated at check point</p>
                <button>Check out</button>
                <h4>Continue Shopping</h4>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

function mapDispatchToProps(dispatch: any) {
  return {
    setCategoryTitle: (category: string) =>
      dispatch(setCategoryTitle(category)),
    setCurrencyType: (currency: string) =>
      dispatch(setCurrencyType(currency.toString())),
  };
}

function mapStateToProps(state: any) {
  return {
    cart: state.cart,
  };
}

export default connect(null, mapDispatchToProps, mapStateToProps)(Cart);
