import React from "react";
import { useQuery, gql } from "@apollo/client";
import { FirstContainer, Heading } from "../../styles/productsListing";
import { connect } from "react-redux";
import { client } from "../../App";

class ProductsListing extends React.Component<
  { category: string },
  { products: []; category: string }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
      category: this.props.category,
    };
  }
  componentDidUpdate(prevState: any) {
    let category = this.props.category.toString();
    if (prevState.category.toString() !== category) {
      console.log(category);

      client
        .query({
          query: gql`
          query category {
            category(input: { title: "${category}" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                brand
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
              }
            }
          }
        `,
        })
        .then((result) =>
          this.setState({ products: result.data.category.products })
        );
      console.log(this.state.products);
    }
  }

  render() {
    return (
      <>
        <FirstContainer>
          <Heading>All Categories</Heading>
          <div
            style={{
              // marginTop: "15px",
              margin: "auto",
              width: "90%",
              display: "grid",
              gap: "40px",
              marginBottom: "10px",
              gridTemplateColumns: "auto auto auto auto",
            }}
          >
            {this.state.products.length >= 0 &&
              this.state.products.map(
                (x: { name: string; prices: any; gallery: ["strings"] }) => (
                  <div>
                    <div>
                      <img src={x.gallery[0]} alt="pic"></img>
                    </div>
                    <div style={{ color: " #80808091", paddingTop: "15px" }}>
                      {x.name}
                    </div>
                    <div style={{ paddingTop: "5px" }}>
                      {x.prices[0].currency.symbol}
                      {x.prices[0].amount}
                    </div>
                  </div>
                )
              )}
          </div>
        </FirstContainer>
      </>
    );
  }
}

function mapDispatchToProps(state: any) {
  return {
    category: state.action["selectedCategory"],
  };
}

export default connect(mapDispatchToProps, null)(ProductsListing);
