import React from "react";
import {
  DetailsContainer,
  ProductImages,
  ProductImage,
  ProductInfo,
} from "../../styles/productDetails";
import { client } from "../../App";
import { gql } from "@apollo/client";
import { useParams } from "react-router";
import { ProductTypes } from "../../types";
import { selectedProduct } from "../../redux/actions/actions";
import { connect } from "react-redux";

class ProductDetails extends React.Component<
  { params: { id: string } },
  { product: any[] }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      product: [],
    };
    console.log(this.state.product);
    let { id } = this.props.params;

    try {
      client
        .query({
          query: gql`
            query product {
              product(id: "${id}") {
                  id
                  name
                  inStock
                  gallery
                  description
                  category
                  brand
                  attributes {
                    id
                    name
                    type
                    items {
                      displayValue
                      value
                      id
                    }
                  }
                  prices {
                    currency {
                      label
                      symbol
                    }
                    amount
                  }
                }
              }
          `,
        })
        .then((result) => this.state.product.push(result.data.product));
    } catch (error) {
      console.log(error);
    }
    console.log([this.state.product]);
  }

  componentDidMount() {}

  render() {
    // const {name} = this.state.product
    return (
      <>
        <DetailsContainer>
          <>
            {this.state.product[0]?.map((p: { name: string }) => (
              <ProductImages>{p.name}</ProductImages>
            ))}

            <ProductImage>d</ProductImage>

            <ProductInfo>f</ProductInfo>
          </>
        </DetailsContainer>
      </>
    );
  }
}

