import React from "react";
import {
  DetailsContainer,
  ProductImages,
  ProductImage,
  ProductInfo,
} from "../../styles/productDetails";
import { client } from "../../App";
import { gql } from "@apollo/client";
import { useParams } from "react-router";
import DOMPurify from "dompurify";
import parse from "html-react-parser";

interface Product {
  id: string;
  name: string;
  inStock: boolean;
  gallery: [];
  description: string;
  category: string;
  brand: string;
  attributes: [
    {
      id: string;
      name: string;
      type: string;
      items: [
        {
          displayValue: string;
          value: string;
          id: string;
        }
      ];
    }
  ];
  prices: [];
}

const initialProduct: Product = {
  id: "string",
  name: "string",
  inStock: false,
  gallery: [],
  description: "string",
  category: "string",
  brand: "string",
  attributes: [
    {
      id: "string",
      name: "string",
      type: "string",
      items: [
        {
          displayValue: "",
          value: "",
          id: "",
        },
      ],
    },
  ],
  prices: [],
};

const Get_Product = (id: string) =>
  client.query({
    query: gql`
      query product($id: String!) {
        product(id: $id) {
          id
          name
          inStock
          gallery
          description
          category
          brand
          attributes {
            id
            name
            type
            items {
              displayValue
              value
              id
            }
          }
          prices {
            currency {
              label
              symbol
            }
            amount
          }
        }
      }
    `,
    variables: { id: id },
  });

class ProductDetails extends React.Component<
  { params: { id: string }; query: { data: any } },
  { product: Product; imagePreview: any }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      product: { ...initialProduct },
      imagePreview: "",
    };
  }
  componentDidMount() {
    let { id } = this.props.params;
    Get_Product(id).then((res) => {
      this.setState({
        product: res.data.product,
        imagePreview: res.data.product.gallery[0],
      });
    });
    // console.log(this.state.product);
  }

  render() {
    let cleanHTML = DOMPurify.sanitize(this.state.product.description, {
      USE_PROFILES: { html: true },
    });
    return (
      <>
        <DetailsContainer>
          <>
            <ProductImages>
              {this.state.product &&
                this.state.product.gallery?.map((img) => (
                  <img
                    key={img}
                    src={img}
                    alt="product-img"
                    height={150}
                    width={200}
                    onClick={() => this.setState({ imagePreview: img })}
                    style={{ cursor: "pointer" }}
                  />
                ))}
            </ProductImages>

            <ProductImage>
              <img
                src={this.state.imagePreview}
                alt="product-preview"
                height={500}
                width={650}
              />
            </ProductImage>
            <ProductInfo>
              <div>
                <h2>
                  <b>{this.state.product.brand}</b>
                </h2>
                <p>{this.state.product.name}</p>
              </div>
              {this.state.product.attributes.map((attr) => (
                <>
                  <p>
                    <b>{attr.name}:</b>
                  </p>
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-evenly",
                    }}
                  >
                    {attr.type === "swatch" &&
                      attr.items.map((itm) => (
                        <button
                          key={itm.id}
                          style={{
                            backgroundColor: `${itm.id}`,
                            height: "30px",
                            width: "45px",
                            border: "1px solid black",
                            cursor: "pointer",
                          }}
                        ></button>
                      ))}

                    {attr.type !== "swatch" &&
                      attr.items.map((itm) => (
                        <button
                          key={itm.id}
                          style={{
                            backgroundColor: "#fff",
                            height: "30px",
                            width: "45px",
                            border: "1px solid black",

                            cursor: "pointer",
                            textAlign: "center",
                          }}
                        >
                          {itm.value}
                        </button>
                      ))}
                  </div>
                </>
              ))}
              <p>Price:</p>
              <p></p>
              <button
                type="submit"
                style={{
                  width: "100%",
                  height: "35px",
                  color: "#fff",
                  backgroundColor: "green",
                  border: "none",
                  textAlign: "center",
                  cursor: "pointer",
                }}
              >
                ADD TO CART
              </button>
              {/* {parse(${cleanHTML})} */}

              {parse(cleanHTML)}
            </ProductInfo>
          </>
        </DetailsContainer>
      </>
    );
  }
}

// eslint-disable-next-line import/no-anonymous-default-export
export default (props: any) => (
  <ProductDetails {...props} params={useParams()} />
);
