import React from "react";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";

import {
  Container,
  Heading,
  CardWrapper,
  CardTextWrapper,
  CardTextDate,
  CardTextTitle,
  CardTextBody,
} from "../../styles/productsListing";
import Tilt from "react-parallax-tilt";

class ProductsListing extends React.Component<
  {},
  { products: []; amount: number }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      amount: 0,
      products: [],
    };
    const client = new ApolloClient({
      uri: "http://localhost:4000/",
      cache: new InMemoryCache(),
    });

    client
      .query({
        query: gql`
          query categories {
            categories {
              products {
                id
                name
                inStock
                gallery
                description
                category
                brand
              }
            }
          }
        `,
      })
      .then((result) => this.setState({ products: result.data.categories }));
  }

  render() {
    return (
      <>
        <div>{this.state.products}</div>
        <Container>
          <Heading>Category name</Heading>
