import React from "react";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import { CardType } from "../../types";

import {
  Container,
  Heading,
  CardWrapper,
  CardTextWrapper,
  CardTextDate,
  CardTextTitle,
  CardTextBody,
} from "../../styles/productsListing";
import Tilt from "react-parallax-tilt";

class ProductsListing extends React.Component<{}, CardType> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
    };
    const client = new ApolloClient({
      uri: "http://localhost:4000/",
      cache: new InMemoryCache(),
    });

    client
      .query({
        query: gql`
          query categories {
            categories {
              products {
                id
                name
                inStock
                gallery
                description
                category
                brand
              }
            }
          }
        `,
      })
      .then((result) =>
        // this.setState({ products: result.data.categories })
        console.log(result)
      );
    console.log(this.state.products);
  }
  componentDidMount() {
    // getData()
  }

  render() {
    return (
      <>
        <div>{this.state.products}</div>
        <Container>
          <Heading>Category name</Heading>
          <Tilt></Tilt>
        </Container>
      </>
    );
  }
}

export default ProductsListing;
