import React from "react";
import {
  DetailsContainer,
  ProductImages,
  ProductImage,
  ProductInfo,
} from "../../styles/productDetails";
import { client } from "../../App";
import { gql } from "@apollo/client";
import { useParams } from "react-router";

interface Product {
  id: string;
  name: string;
  inStock: boolean;
  gallery: [];
  description: string;
  category: string;
  brand: string;
  attributes: [
    {
      id: string;
      name: string;
      type: string;
      items: [
        {
          displayValue: string;
          value: string;
          id: string;
        }
      ];
    }
  ];
  prices: [];
}

const initialProduct: Product = {
  id: "string",
  name: "string",
  inStock: false,
  gallery: [],
  description: "string",
  category: "string",
  brand: "string",
  attributes: [
    {
      id: "string",
      name: "string",
      type: "string",
      items: [
        {
          displayValue: "",
          value: "",
          id: "",
        },
      ],
    },
  ],
  prices: [],
};

const Get_Product = (id: string) =>
  client.query({
    query: gql`
      query product($id: String!) {
        product(id: $id) {
          id
          name
          inStock
          gallery
          description
          category
          brand
          attributes {
            id
            name
            type
            items {
              displayValue
              value
              id
            }
          }
          prices {
            currency {
              label
              symbol
            }
            amount
          }
        }
      }
    `,
    variables: { id: id },
  });

class ProductDetails extends React.Component<
  { params: { id: string }; query: { data: any } },
  { product: Product; imagePreview: any }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      product: { ...initialProduct },
      imagePreview: "",
    };
  }
  componentDidMount() {
    let { id } = this.props.params;
    Get_Product(id).then((res) => {
      this.setState({
        product: res.data.product,
        imagePreview: res.data.product.gallery[0],
      });
    });
  }

  render() {
    return (
      <>
        <DetailsContainer>
          <>
            <ProductImages>
              {this.state.product &&
                this.state.product.gallery?.map((img) => (
                  <img
                    key={img}
                    src={img}
                    alt="product-img"
                    height={150}
                    width={200}
                    onClick={() => this.setState({ imagePreview: img })}
                  />
                ))}
            </ProductImages>

            <ProductImage>
              <img
                src={this.state.imagePreview}
                alt="product-preview"
                height={500}
                width={650}
              />
            </ProductImage>
            <ProductInfo>
              <div>
                <h2>
                  <b>{this.state.product.brand}</b>
                </h2>
                <p>{this.state.product.name}</p>
              </div>
              <p>
                <b>
                  {this.state.product.attributes.map((attr) => (
                    <>
                      <p>{attr.name}:</p>
                      {attr.items.map((itm) => (
                        <button
                          style={{
                            backgroundColor: `${itm.id}`,
                          }}
                        ></button>
                      ))}
                    </>
                  ))}
                </b>
              </p>
            </ProductInfo>
          </>
        </DetailsContainer>
      </>
    );
  }
}

// eslint-disable-next-line import/no-anonymous-default-export
export default (props: any) => (
  <ProductDetails {...props} params={useParams()} />
);
