import React from "react";
import { connect } from "react-redux";
import { setCategory, setCurrency } from "../../redux/selectSlice";

import { addToCart, decreaseCart, getTotals } from "../../redux/cartSlice";
import { Attribute, AttributeButton } from "../../styles/productDetails";
import { CartTypes, ItemProps } from "../../types";
import { FaLessThan, FaGreaterThan } from "react-icons/fa";

class CartOverlay extends React.Component<
  {
    cart: [];
    decreaseCart: any;
    addToCart: any;
    totalQuantity: number;
    totalAmount: number;
    getTotals: any;
    currency: string;
  },
  CartTypes
> {
  constructor(props: any) {
    super(props);
    this.state = {
      cartItems: this.props.cart,
      categories: [],
      category: "all",
      currencies: [],
      currency: "USD",
      imageIndex: 0,
      num: 0,
    };
  }

  decreaseCartItemQuantity(item: any) {
    this.props.decreaseCart(item);
    this.props.getTotals();
  }

  increaseCartItemQuantity(item: any) {
    this.props.addToCart(item);
    this.props.getTotals();
  }

  changeImagePreviewUp(length: number, id: string) {
    // const existingIndex = state.cartItems.findIndex(
    //   (item:any) => item.id === payload.id
    // );
    let index = 0;
    // for (let index = 0; index < length; index++) {
    // this.setState({ num:});
    // console.log(this.state.num);
    const numn = this.state.num + 1;
    console.log(numn);
    // }
  }

  changeImagePreviewDown(length: number, id: string) {
    for (let index = 0; index < length; index++) {
      this.setState({ num: index-- });
    }
  }

  componentDidMount() {}

  render() {
    return (
      <>
        <div
          style={{
            width: "88%",
            margin: "auto 10px",
            backgroundColor: "#fff",
          }}
        >
          <b>My Bag,</b> {this.props.totalQuantity} items
          <br />
          {this.state.cartItems?.length > 0 ? (
            this.state.cartItems.map((item: ItemProps) => (
              <>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    marginBottom: "15px",
                  }}
                  key={item.id}
                >
                  <div>
                    <p>{item.name}</p>
                    <p>{item.brand}</p>
                    {item.prices?.map(
                      (p) =>
                        p.currency.label === this.props.currency && (
                          <p>
                            <b>
                              {p.currency.symbol}
                              {p.amount}
                            </b>
                          </p>
                        )
                    )}

                    <div>
                      {item.attributes.map((attr: any) => (
                        <>
                          <Attribute>{attr.name}:</Attribute>
                          <div
                            style={{
                              display: "flex",
                              gap: "1px",
                            }}
                          >
                            {attr.type !== "swatch" &&
                              attr.items.map((itm: any) => (
                                <AttributeButton key={itm.id}>
                                  {itm.value}
                                </AttributeButton>
                              ))}
                            {attr.type === "swatch" &&
                              attr.items.map((itm: any) => (
                                <AttributeButton
                                  key={itm.id}
                                  style={{
                                    backgroundColor: `${itm.id}`,
                                    height: "20px",
                                    width: "20px",
                                    border: "1px solid black",
                                    cursor: "pointer",
                                  }}
                                ></AttributeButton>
                              ))}
                          </div>
                        </>
                      ))}
                    </div>
                  </div>
                  <div
                    style={{
                      display: "flex",
                      gap: "5px",
                      lineHeight: "60px",
                      maxHeight: "100%",
                    }}
                  >
                    <div>
                      <button
                        onClick={() => this.increaseCartItemQuantity(item)}
                        style={{
                          width: "25px",
                          height: "20px",
                          backgroundColor: "#fff",
                          border: "1px solid black",
                          cursor: "pointer",
                        }}
                      >
                        <span style={{ fontSize: "15px" }}>+</span>
                      </button>
                      <h4 style={{ textAlign: "center" }}>
                        {item.cartQuantity}
                      </h4>
                      <button
                        style={{
                          width: "25px",
                          height: "20px",
                          backgroundColor: "#fff",
                          border: "1px solid black",
                          cursor: "pointer",
                        }}
                        onClick={() => this.decreaseCartItemQuantity(item)}
                      >
                        <span style={{ fontSize: "15px" }}>-</span>
                      </button>
                    </div>
                    <div
                      style={{
                        backgroundColor: "#fff",
                        position: "relative",
                      }}
                    >
                      <img
                        src={item.gallery[this.state.num]}
                        width={150}
                        height={150}
                        style={{ objectFit: "fill" }}
                        alt="itemImg"
                      />
                      <div
                        style={{
                          display: "flex",
                          gap: "4px",
                          position: "absolute",
                          bottom: "15%",
                          right: "6%",
                        }}
                      >
                        <FaLessThan
                          onClick={() =>
                            this.changeImagePreviewDown(
                              item.gallery.length,
                              item.id
                            )
                          }
                          style={{ backgroundColor: "black", color: "white" }}
                        />
                        <FaGreaterThan
                          onClick={() =>
                            this.changeImagePreviewUp(
                              item.gallery.length,
                              item.id
                            )
                          }
                          style={{ backgroundColor: "black", color: "white" }}
                        />
                      </div>
                    </div>
                  </div>
                </div>
                <hr />
              </>
            ))
          ) : (
            <div>No Items in Cart</div>
          )}
          {this.props.cart?.length > 0 && (
            <div
              style={{
                display: "grid",
                marginTop: "30px",
              }}
            >
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <span>Total</span>
                <b>{this.props.totalAmount}</b>
              </div>
              <div>
                <button
                  style={{
                    width: "132px",
                    height: "30px",
                    backgroundColor: "green",
                    color: "white",
                    border: "none",
                  }}
                >
                  Order
                </button>
                <button
                  style={{
                    width: "132px",
                    height: "30px",
                    backgroundColor: "green",
                    color: "white",
                    border: "none",
                  }}
                >
                  Order
                </button>
              </div>
            </div>
          )}
        </div>
      </>
    );
  }
}

function mapDispatchToProps(dispatch: any) {
  return {
    decreaseCart: (product: any) => dispatch(decreaseCart(product)),
    addToCart: (product: any) => dispatch(addToCart(product)),
    getTotals: () => dispatch(getTotals(0)),
  };
}

function mapStateToProps(state: any) {
  return {
    cart: state.cart.cartItems,
    totalQuantity: state.cart.cartTotalQuantity,
    totalAmount: state.cart.cartTotalAmount,
    currency: state.selection["selectedCurrency"],
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CartOverlay);
