import React from "react";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  useQuery,
  gql,
} from "@apollo/client";
import { BsCart } from "react-icons/bs";
import {
  TopHeading,
  Item,
  Logo,
  Menu,
  SingleIcon,
  DropDownContent,
  DropItem,
} from "../../styles/topNav";

class TopNav extends React.Component<
  {},
  {
    products: [];
    numbers: number[];
    categories: [];
    category: string;
  }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
      numbers: [1, 2, 3, 4, 5, 6, 7],
      categories: [],
      category: "all",
    };
  }
  selectedCategoryTitle(title: string) {
    this.setState({ category: title });
  }
  componentDidMount() {
    const client = new ApolloClient({
      uri: "http://localhost:4000/",
      cache: new InMemoryCache(),
    });

    console.log(this.state.category);
    client
      .query({
        query: gql`
          query categories {
            categories {
              name
            }
          }
        `,
      })
      .then((result) => this.setState({ categories: result.data.categories }));
  }

  render() {
    return (
      <>
        <TopHeading>
          <Menu>
            {this.state.categories.length > 0 &&
              this.state.categories?.map((category: any) => (
                <Item
                  id={category.name}
                  onClick={(e: any) => this.selectedCategoryTitle(e.target.id)}
                >
                  {category.name}
                </Item>
              ))}
          </Menu>

          <Logo>Logo</Logo>

          <div>
            <SingleIcon data-badge="2">
              <BsCart />
            </SingleIcon>

            <SingleIcon>
              <DropDownContent>
                <DropItem> $ USD </DropItem>
                <DropItem>&#8364; EUR </DropItem>
                <DropItem>&#165; JPY</DropItem>
              </DropDownContent>
            </SingleIcon>
          </div>
        </TopHeading>
      </>
    );
  }
  setCategory(id: any): void {
    throw new Error("Method not implemented.");
  }
}

export default TopNav;
