import React from "react";
import { gql } from "@apollo/client";
import {
  FirstContainer,
  Heading,
  CardsContainer,
  Card,
  WaterMark,
  CardTitle,
  Cart,
  Banner,
} from "../../styles/productsListing";
import { connect } from "react-redux";
import { client } from "../../App";
import { CardPropsTypes } from "../../types";
import { BsCart } from "react-icons/bs";
import { Link } from "react-router-dom";

class ProductsListing extends React.Component<
  { category: string },
  { products: []; num: number }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
      num: 0,
    };
  }
  componentDidMount() {
    let category = this.props.category;
    client
      .query({
        query: gql`
          query category {
            category(input: { title: "${category}" }) {
              name
              products {
                id
                name
                inStock
                gallery
                description
                category
                brand
                attributes {
                  id
                  name
                  type
                  items {
                    displayValue
                    value
                    id
                  }
                }
                prices {
                  currency {
                    label
                    symbol
                  }
                  amount
                }
              }
            }
          }
        `,
      })
      .then((result) =>
        this.setState({
          products: result.data.category.products,
        })
      );
  }

  // componentDidUpdate(prevState: any) {
  //   let category = this.props.category;
  //   if (prevState.category !== category || prevState.currency !== currency) {
  //   }

  // }

  render() {
    let numm = this.state.num;
    return (
      <>
        <FirstContainer>
          <Heading>All Categories</Heading>
          <CardsContainer>
            {this.state.products.length >= 0 &&
              this.state.products.map((x: CardPropsTypes) => (
                <Card key={x.name}>
                  <Banner>
                    {x.inStock === false && <WaterMark>Out of stock</WaterMark>}
                    <img
                      src={x.gallery[0]}
                      height={250}
                      width={250}
                      alt="Product Img"
                    />
                    <Link to={`/product/${x.id}`}>
                      <Cart>
                        <BsCart />
                      </Cart>
                    </Link>
                  </Banner>

                  <CardTitle>{x.name}</CardTitle>
                  <div style={{ paddingTop: "5px" }}>
                    {x.prices[numm].currency.symbol}
                    {x.prices[numm].amount}
                    <br />
                  </div>
                </Card>
              ))}
          </CardsContainer>
        </FirstContainer>
      </>
    );
  }
}

export default ProductsListing;
