import { createSlice } from "@reduxjs/toolkit";
import { toast } from "react-toastify";


const initialState = {
  cartItems: localStorage.getItem("cartItems")
    ? JSON.parse(localStorage.getItem("cartItems")!)
    : [],
  cartTotalQuantity: 0,
  cartTotalAmount: "",
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addToCart: (state, {payload,type}) => {
      console.log(typeof payload)
      const existingIndex = state.cartItems.findIndex(
        (item:any) => item.id === payload.product.id
      );

      if (existingIndex >= 0) {
        state.cartItems[existingIndex] = {
          ...state.cartItems[existingIndex],
          cartQuantity: state.cartItems[existingIndex].cartQuantity += 1,
        };
        toast.info(`${payload.product.name} quantity increased`, {
          position: "top-center",
        })
      } else {
        let tempProductItem = { ...payload.product, cartQuantity: 1, imageIndex: 0,selectedAttributes:payload.attr };
        state.cartItems.push(tempProductItem);
        toast.success(`${payload.product.name} added to cart`, {
          position: "top-center",
        })
      }
      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    decreaseCart(state, {payload, type}) {
      const itemIndex = state.cartItems.findIndex(
        (item:any) => item.id === payload.id
      );

      if (state.cartItems[itemIndex].cartQuantity > 1) {
        state.cartItems[itemIndex].cartQuantity -= 1;

        toast.info(`${payload.name} quantity decreased in cart`, {
          position: "top-center",
      })
      } else if (state.cartItems[itemIndex].cartQuantity === 1) {
        const nextCartItems = state.cartItems.filter(
          (item:any) => item.id !== payload.id
        );

        state.cartItems = nextCartItems;

        toast.error(`${payload.name} removed from cart`, {
          position: "top-center",
        })
      }

      localStorage.setItem("cartItems", JSON.stringify(state.cartItems));
    },
    getTotals(state) {
      let totalPrice = 0;
      let total = 0
      let currencySymbol = ""

      let { quantity } = state.cartItems.reduce(
        (cartTotal:any, cartItem:any) => {
          const {cartQuantity } = cartItem;
          cartTotal.quantity += cartQuantity;       
          return cartTotal;
        },
        {
          quantity: 0,
        }
        );
        state.cartItems.forEach((i: any) => {
          const pricesProxy = JSON.parse(JSON.stringify(i.prices))
          const amount = pricesProxy.filter((x: any) => x.currency.label === localStorage.getItem("currency"))[0]
          total += amount.amount * i.cartQuantity
          currencySymbol = amount.currency.symbol
        })
        totalPrice = parseFloat(total.toFixed(2));
        state.cartTotalAmount = currencySymbol + totalPrice;
        state.cartTotalQuantity = quantity;
      },

      // selectAttribute(state, {payload}) {
      //   console.log(payload.product)
      //   const existingIndex = state.cartItems.findIndex(
      //     (item:any) => item.id === payload.product.item?.id
      //   ); 
      //   const item = state.cartItems.map(
      //     (item:any) => item.id === payload.product.item?.id
      //   );
      //   if (existingIndex >= 0) {
      //     if(item)
      //    state.cartItems[existingIndex].selectedAttribute.push(payload.product.attr)    
      // }
      // localStorage.setItem("cartItems", JSON.stringify(state.cartItems));

      // },
  }
});

export const { addToCart, decreaseCart,  getTotals } =
  cartSlice.actions;

export default cartSlice.reducer;
