import React from "react";
import { useQuery, gql } from "@apollo/client";
import { FirstContainer, Heading } from "../../styles/productsListing";
import { connect } from "react-redux";
import { client } from "../../App";

class ProductsListing extends React.Component<{}, { products: [] }> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
    };
    client
      .query({
        query: gql`
          query category {
            category(input: {title:}) {
              name
    products {
      id
      name
      inStock
      gallery
      description
      category
      brand
      attributes{
        id
        name
        type
        items {
          displayValue
          value
          id
        }
      }
      prices {
        currency {
          label
          symbol
        }
        amount
      }

    }
            }
          }

        `,
      })
      .then((result) =>
        // this.setState({ products: result.data.categories })
        console.log(result)
      );
  }

  render() {
    return (
      <>
        <FirstContainer>
          <Heading>All Categories</Heading>
          <div
            style={{
              // marginTop: "15px",
              margin: "auto",
              width: "90%",
              display: "grid",
              gap: "40px",
              marginBottom: "10px",
              gridTemplateColumns: "auto auto auto auto",
            }}
          >
            {this.state.products.length >= 0 &&
              this.state.products.map((x) => (
                <div>
                  <div>
                    <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                  </div>
                  <div style={{ color: " #80808091", paddingTop: "15px" }}>
                    Apollo Running Short
                  </div>
                  <div style={{ paddingTop: "5px" }}>$50.00</div>
                </div>
              ))}
          </div>
        </FirstContainer>
      </>
    );
  }
}

export default ProductsListing;
