import React from "react";
import { useQuery as query } from "@apollo/client";
import {
  Container,
  Heading,
  CardWrapper,
  CardTextWrapper,
  CardTextDate,
  CardTextTitle,
  CardTextBody,
} from "../../styles/productsListing";
import Tilt from "react-parallax-tilt";
import { Categories } from "../../queries/queries";

class ProductsListing extends React.Component {
  constructor(props: any) {
    super(props);
    this.state = {
      amount: 0,
    };
  }

  render() {
    const { loading, error, data } = query(Categories);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    return (
      <>
        <Container>
          <Heading>Category name</Heading>
          <Tilt>
            <CardWrapper>
              <CardTextWrapper>
                <CardTextDate>9 days ago</CardTextDate>
                <CardTextTitle>title</CardTextTitle>
                <CardTextBody>
                  Lorem ipsum dolor sit amet consectetur, Ducimus, repudiandae
                  temporibus omnis illum maxime quod deserunt eligendi dolor
                </CardTextBody>
              </CardTextWrapper>
            </CardWrapper>
          </Tilt>
        </Container>
      </>
    );
  }
}

export default ProductsListing;
