import styled from "styled-components"

export const TopHeading = styled.div`
  display: flex;
  background-color: white;
  justify-content:space-between;
`

export const Items = styled.p`
  font-size: 15px;
  color: black;
  &:hover{
    text-decoration: underline;
    color: green;
  }
  &:active{
    color:green;
  }
  cursor:pointer;
  padding: 0 10px;
`

export const Menu = styled.p`
  font-size: 15px;
  color: blue;
  display: inline-flex;
`

export const Logo = styled.p`
  font-size: 15px;
  color: blue;
`
export const SingleIcon = styled.div`
  font-size: 15px;
  color: black;
  float:right;
  padding: 3px 10px;
`

export const DropDownContent = styled.p`
list-style-type: none;
box-shadow: 50px 50px 50px 50px #f9f9f9;
`
export const DropItem = styled.p`
padding: 3px;
`
