import React from "react";
import { connect } from "react-redux";
import { setCategory, setCurrency } from "../../redux/selectSlice";

import { addToCart, decreaseCart } from "../../redux/cartSlice";
import { Attribute, AttributeButton } from "../../styles/productDetails";
import { CartTypes, ItemProps } from "../../types";
import {
  BsFillArrowLeftSquareFill,
  BsFillArrowRightSquareFill,
} from "react-icons/bs";

class Cart extends React.Component<
  {
    setCategoryTitle: any;
    setCurrencyType: any;
    cart: any;
    decreaseCart: any;
    addToCart: any;
  },
  CartTypes
> {
  constructor(props: any) {
    super(props);
    this.state = {
      cartItems: this.props.cart,
      categories: [],
      category: "all",
      currencies: [],
      currency: "USD",
      imagePreview: "",
      num: 0,
    };
  }

  decreaseCartItemQuantity(item: any) {
    this.props.decreaseCart(item);
  }

  increaseCartItemQuantity(item: any) {
    this.props.addToCart(item);
  }

  changeImagePreviewUp(length: number) {
    let index = 0;
    // for (let index = 0; index < length; index++) {
    this.setState({ num: index++ });
    // console.log(index);
    // }
  }

  changeImagePreviewDown(length: number) {
    for (let index = 0; index < length; index++) {
      this.setState({ num: index-- });
    }
  }

  componentDidMount() {}

  render() {
    return (
      <>
        <div
          style={{
            width: "88%",
            margin: "auto",
            backgroundColor: "#fff",
          }}
        >
          <h3>CART</h3>
          <br />
          <hr />
          {this.state.cartItems?.length > 0 ? (
            this.state.cartItems.map((item: ItemProps) => (
              <>
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    marginBottom: "15px",
                  }}
                  key={item.id}
                >
                  <div>
                    <h2>
                      <b>{item.name}</b>
                    </h2>
                    <p>{item.brand}</p>
                    <p>
                      <b>$50.00</b>
                    </p>
                    <div>
                      {item.attributes.map((attr: any) => (
                        <>
                          <Attribute>{attr.name}:</Attribute>
                          <div
                            style={{
                              display: "flex",
                              gap: "10px",
                            }}
                          >
                            {attr.type === "swatch" &&
                              attr.items.map((itm: any) => (
                                <AttributeButton
                                  key={itm.id}
                                  style={{
                                    backgroundColor: `${itm.id}`,
                                    height: "20px",
                                    width: "20px",
                                    border: "1px solid black",
                                    cursor: "pointer",
                                  }}
                                ></AttributeButton>
                              ))}

                            {attr.type !== "swatch" &&
                              attr.items.map((itm: any) => (
                                <AttributeButton key={itm.id}>
                                  {itm.value}
                                </AttributeButton>
                              ))}
                          </div>
                        </>
                      ))}
                    </div>
                  </div>
                  <div
                    style={{
                      display: "flex",
                      gap: "10px",
                      lineHeight: "90px",
                      maxHeight: "100%",
                    }}
                  >
                    <div>
                      <button
                        onClick={() => this.increaseCartItemQuantity(item)}
                        style={{
                          width: "30px",
                          height: "30px",
                          backgroundColor: "#fff",
                          border: "1px solid black",
                          cursor: "pointer",
                        }}
                      >
                        <span style={{ fontSize: "25px" }}>+</span>
                      </button>
                      <h4 style={{ textAlign: "center" }}>
                        {item.cartQuantity}
                      </h4>
                      <button
                        style={{
                          width: "30px",
                          height: "30px",
                          backgroundColor: "#fff",
                          border: "1px solid black",
                          cursor: "pointer",
                        }}
                        onClick={() => this.decreaseCartItemQuantity(item)}
                      >
                        <span style={{ fontSize: "25px" }}>-</span>
                      </button>
                    </div>
                    <div
                      style={{
                        backgroundColor: "#fff",
                        position: "relative",
                      }}
                    >
                      <img
                        src={item.gallery[this.state.num]}
                        width={200}
                        height={250}
                        style={{ objectFit: "fill" }}
                        alt="itemImg"
                      />
                      <div
                        style={{
                          display: "flex",
                          gap: "4px",
                          position: "absolute",
                          bottom: "15%",
                          right: "6%",
                        }}
                      >
                        <BsFillArrowLeftSquareFill
                          onClick={() =>
                            this.changeImagePreviewDown(item.gallery.length)
                          }
                        />
                        <BsFillArrowRightSquareFill
                          onClick={() =>
                            this.changeImagePreviewUp(item.gallery.length)
                          }
                        />
                      </div>
                    </div>
                  </div>
                </div>
                <hr />
              </>
            ))
          ) : (
            <div>No Items in Cart</div>
          )}
          {this.props.cart?.length > 0 && (
            <div
              style={{
                display: "grid",
                marginTop: "30px",
              }}
            >
              <span>
                Tax: <b>15$</b>
              </span>
              <span>
                Qty: <b>3</b>
              </span>
              <div>
                <span>
                  Total: <b>65$</b>
                </span>
              </div>
              <button
                style={{
                  width: "132px",
                  height: "30px",
                  backgroundColor: "green",
                  color: "white",
                  border: "none",
                }}
              >
                Order
              </button>
            </div>
          )}
        </div>
      </>
    );
  }
}

function mapDispatchToProps(dispatch: any) {
  return {
    setCategoryTitle: (category: string) => dispatch(setCategory(category)),
    setCurrencyType: (currency: string) =>
      dispatch(setCurrency(currency.toString())),
    decreaseCart: (product: any) => dispatch(decreaseCart(product)),
    addToCart: (product: any) => dispatch(addToCart(product)),
  };
}

function mapStateToProps(state: any) {
  return {
    cart: state.cart.cartItems,
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
