import { ActionTypes } from "../constants/action-types"

const initialState = {
  products:[],
  selectedCategory:"",
  selectedCurrency:"",
  cart:{
    cartItems: [],
    cartTotalQuantity:0,
    cartTotalAmount: 0,
  }
}

export const productsReducer = (state=initialState, {type,payload}) => {
  switch (type) {
    case ActionTypes.SET_CATEGORY_TITLE:
      return {
        selectedCategory: [payload]
      }
      case ActionTypes.SET_CURRENCY_TYPE:
        return {
          selectedCurrency: [payload]
        }
    default:
      return state;
  }
}

export const cartReducer = (state=initialState.cart, {type,payload}) => {
  switch (type) {
    case ActionTypes.ADD_TO_CART:
      const productIndex = state.cartItems.findIndex( item => item.id === payload.id);
      if(productIndex >= 0) {
        state.cartItems[productIndex].cartQuantity += 1
      } else {
        const tempProduct = {...payload, cartQuantity: 1}
        return {...state, cartItems:state.cartItems.concat(tempProduct), cartTotalQuantity:state.cartItems.length};
      }
      return {...state}
    case ActionTypes.REMOVE_FROM_CART:
      return {};
    default:
      return state;
  }
}
