import { parse } from "graphql";
import {
  NoStyleDiv,
  ProductBrand,
  ProductName,
  AttributeSwatch,
  AttributeButton,
  Attribute,
  ProductPrice,
} from "../../styles/cart";
import {
  ProductInfo,
  AttributeName,
  AttributeStyle,
  AddButton,
  OutOfStock,
  ProductParse,
} from "../../styles/productDetails";
import * as React from "react";

class Details extends React.Component {
  constructor(props: any) {
    super(props);
  }

  render() {
    return (
      <ProductInfo>
        <NoStyleDiv>
          <ProductBrand>{this.state.product.brand}</ProductBrand>
          <ProductName>{this.state.product.name}</ProductName>
        </NoStyleDiv>

        {this.state.product.attributes.map((attr) => (
          <NoStyleDiv key={attr.id}>
            <AttributeName>{attr.name}:</AttributeName>
            <AttributeStyle>
              {attr.type === "swatch" &&
                attr.items.map((itm) => (
                  <AttributeSwatch
                    color={`${itm.id}`}
                    key={itm.id}
                    className={
                      itm.id === this.state.attributes[attr.name]
                        ? "swatchSelected"
                        : null
                    }
                    onClick={() => this.setAttributes(itm, attr.name)}
                  />
                ))}

              {attr.type !== "swatch" &&
                attr.items.map((itm) => (
                  <AttributeButton
                    key={itm.id}
                    className={
                      itm.id === this.state.attributes[attr.name]
                        ? "selected"
                        : null
                    }
                    onClick={() => this.setAttributes(itm, attr.name)}
                  >
                    {itm.value}
                  </AttributeButton>
                ))}
            </AttributeStyle>
          </NoStyleDiv>
        ))}

        <Attribute>PRICE:</Attribute>
        {this.state.product.prices.map(
          (p) =>
            p.currency.symbol === this.props.currency && (
              <ProductPrice key={p.currency.symbol}>
                {p.currency.symbol}
                {p.amount}
              </ProductPrice>
            )
        )}

        {this.state.product.inStock === true && (
          <AddButton
            type="submit"
            onClick={() => this.addProductToCart(this.state.product)}
          >
            ADD TO CART
          </AddButton>
        )}
        {this.state.product.inStock !== true && (
          <OutOfStock type="button">OUT OF STOCK</OutOfStock>
        )}
        <ProductParse>{parse(cleanHTML)}</ProductParse>
      </ProductInfo>
    );
  }
}

export default Details;
