import React from "react";
import {
  FirstContainer,
  Heading,
  CardsContainer,
  Card,
  WaterMark,
  CardTitle,
  Cart,
  Banner,
  ProductPrice,
  ProductInfo,
} from "../../styles/productsListing";
import { connect } from "react-redux";
import { CardPropsTypes } from "../../types";
import cart from "../../img/cart.svg";
import { Link } from "react-router-dom";
import { Get_ProductList } from "../../queries";
import { addToCart, getTotals } from "../../redux/cartSlice";

class ProductsListing extends React.Component<
  { category: string; currency: string; addToCart: any; getTotals: any },
  { products: []; category: string; currency: string; attribute: any }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
      category: this.props.category,
      currency: this.props.currency,
      attribute: {},
    };
  }

  addProductToCart(product: { attributes: [] }) {
    const predefinedAttributes = () => {
      const attributeId = product.attributes.map((attr: any) => attr.id);
      const itemAttributes = product.attributes.map((attr) => attr);
      const attribute = itemAttributes.map((itm: any) => itm.items[0].id);
      let ab = {};
      attributeId.forEach((value1, index) => {
        const value2 = attribute[index];
        let a = { [value1]: value2 };
        ab = Object.assign(ab, a);
      });
      this.setState({ attribute: ab });
      console.log(this.state.attribute);
    };

    let attribute = this.state.attribute;
    predefinedAttributes();

    this.props.addToCart({ product, attribute });
    this.props.getTotals();
  }

  componentDidMount() {
    let category = this.props.category;
    try {
      Get_ProductList(category).then((result) => {
        this.setState({
          products: result.data.category?.products,
          category: result.data.category?.name,
        });
      });
    } catch (error) {
      console.log(error);
    }
  }

  componentDidUpdate(prevState: any) {
    let currency = this.props.currency;
    let category = this.props.category;
    if (prevState.category !== category || prevState.currency !== currency) {
      Get_ProductList(category).then((result) => {
        this.setState({
          products: result.data.category.products,
        });
      });
    }
  }

  render() {
    return (
      <FirstContainer>
        <Heading>{this.props.category}</Heading>
        <CardsContainer>
          {this.state.products?.length >= 0 &&
            this.state.products.map((x: CardPropsTypes) => (
              <Card key={x.name}>
                <Banner>
                  <Link to={`/product/${x.id}`}>
                    <img
                      src={x.gallery[0]}
                      height={320}
                      width={400}
                      alt="Product Img"
                    />
                    {x.inStock === false && <WaterMark>Out of stock</WaterMark>}
                  </Link>
                  {x.inStock === true && (
                    <Cart onClick={() => this.addProductToCart(x)}>
                      <img src={cart} alt="add product" />
                    </Cart>
                  )}
                </Banner>
                <Link
                  to={`/product/${x.id}`}
                  style={{ textDecoration: "none" }}
                >
                  <ProductInfo>
                    <CardTitle>
                      {x.brand} ~ {x.name}
                    </CardTitle>
                    {x.prices.map(
                      (p: any) =>
                        p.currency.symbol === this.props.currency && (
                          <ProductPrice key={p.currency.symbol}>
                            {p.currency.symbol}
                            {p.amount}
                          </ProductPrice>
                        )
                    )}
                  </ProductInfo>
                </Link>
              </Card>
            ))}
        </CardsContainer>
      </FirstContainer>
    );
  }
}

export function mapStateToProps(state: any) {
  return {
    category: state.selection["selectedCategory"],
    currency: state.selection["selectedCurrency"],
  };
}

function mapDispatchToProps(dispatch: any) {
  return {
    addToCart: (product: any) => dispatch(addToCart(product)),
    getTotals: () => dispatch(getTotals()),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductsListing);
