            query product {
              product(id: "${id}") {
                  id
                  name
                  inStock
                  gallery
                  description
                  category
                  brand
                  attributes {
                    id
                    name
                    type
                    items {
                      displayValue
                      value
                      id
                    }
                  }
                  prices {
                    currency {
                      label
                      symbol
                    }
                    amount
                  }
                }
              }
          `,
        })
        .then((result) => this.setState({ product: result.data.product }));
    } catch (error) {
      console.log(error);
    }
    console.log(this.state.product);
    console.log(this.state.all);
  }

