import React from "react";
import { connect } from "react-redux";
import { setCategoryTitle, setCurrencyType } from "../../redux/actions/actions";
import { TopNavTypes } from "../../types";

class Cart extends React.Component<
  {
    setCategoryTitle: any;
    setCurrencyType: any;
  },
  TopNavTypes
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: localStorage.getItem("cartItems"),
      categories: [],
      category: "all",
      currencies: [],
      currency: "USD",
    };
  }
  componentDidMount() {
    console.log(typeof this.state.products);
  }

  render() {
    return (
      <>
        <div
          style={{ width: "88%", margin: "10px auto", backgroundColor: "#fff" }}
        >
          <h3>CART</h3>
          <br />
          <hr />

          {this.state.products &&
            this.state.products?.map(
              (item: {
                name:
                  | boolean
                  | React.ReactChild
                  | React.ReactFragment
                  | React.ReactPortal
                  | null
                  | undefined;
              }) => (
                <div
                  style={{ display: "flex", justifyContent: "space-between" }}
                >
                  <div>
                    <h4>{item.name}</h4>
                    <h6>Running Short</h6>
                    <h6>$50.00</h6>
                    <div>
                      <button>S</button>
                      <button>S</button>
                    </div>
                  </div>
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      gap: "10px",
                    }}
                  >
                    <div>
                      <button>+</button>
                      <h4 style={{ textAlign: "center" }}>1</h4>
                      <button>-</button>
                    </div>
                    <div>
                      <p>Subtotal</p>
                    </div>
                  </div>
                </div>
              )
            )}
          <br />
          <br />
          <br />
          <div>
            <div>
              <button>Home</button>
            </div>
            <div>
              <div>
                <p>Subtotal</p>
              </div>
            </div>
          </div>
        </div>
      </>
    );
  }
}

function mapDispatchToProps(dispatch: any) {
  return {
    setCategoryTitle: (category: string) =>
      dispatch(setCategoryTitle(category)),
    setCurrencyType: (currency: string) =>
      dispatch(setCurrencyType(currency.toString())),
  };
}

export default connect(null, mapDispatchToProps)(Cart);
