import React from "react";
import { BsCart } from "react-icons/bs";
import {
  FirstContainer,
  Heading,
  SecondContainer,
  ThirdContainer,
} from "../../styles/productsListing";
import {
  TopHeading,
  Items,
  Logo,
  Menu,
  SingleIcon,
  DropDownContent,
  DropItem,
} from "../../styles/topNav";

class TopNav extends React.Component<
  {},
  { showWomen: boolean; products: []; showMen: boolean; showChildren: boolean }
> {
  constructor(props: any) {
    super(props);
    this.state = {
      products: [],
      showWomen: true,
      showMen: false,
      showChildren: false,
    };
  }
  openWomenCategory = () => {
    this.setState({ showMen: false });
    this.setState({ showWomen: true });
    this.setState({ showChildren: false });
  };
  openMenCategory = () => {
    this.setState({ showMen: true });
    this.setState({ showWomen: false });
    this.setState({ showChildren: false });
  };
  openKidsCategory = () => {
    this.setState({ showMen: false });
    this.setState({ showWomen: false });
    this.setState({ showChildren: true });
  };
  render() {
    return (
      <>
        <TopHeading>
          <Menu>
            <Items onClick={this.openWomenCategory}>WOMEN</Items>
            <Items onClick={this.openMenCategory}>MEN</Items>
            <Items onClick={this.openKidsCategory}>KIDS</Items>
          </Menu>

          <Logo>Logo</Logo>

          <div>
            <SingleIcon data-badge="2">
              <BsCart />
            </SingleIcon>

            <SingleIcon>
              <DropDownContent>
                <DropItem> $ USD </DropItem>
                <DropItem>&#8364; EUR </DropItem>
                <DropItem>&#165; JPY</DropItem>
              </DropDownContent>
            </SingleIcon>
          </div>
        </TopHeading>

        {this.state.showWomen && (
          <FirstContainer>
            <Heading>Category Women</Heading>
            <div
              style={{
                // marginTop: "15px",
                margin: "auto",
                width: "90%",
                display: "flex",
                gap: "3rem",
                overflow: "clip",
              }}
            >
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div style={{ paddingTop: "5px" }}>$50.00</div>
              </div>
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div>$50.00</div>
              </div>
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div>$50.00</div>
              </div>
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div>$50.00</div>
              </div>
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div>$50.00</div>
              </div>
              <div>
                <div>
                  <img src={"https://picsum.photos/250/280"} alt="pic"></img>
                </div>
                <div style={{ color: " #80808091" }}>Apollo Running Short</div>
                <div>$50.00</div>
              </div>
            </div>
          </FirstContainer>
        )}

        {this.state.showMen && (
          <SecondContainer>
            <Heading>Category Men</Heading>
          </SecondContainer>
        )}

        {this.state.showChildren && (
          <ThirdContainer>
            <Heading>Category Children</Heading>
          </ThirdContainer>
        )}
      </>
    );
  }
}

export default TopNav;
