import React, { MouseEventHandler } from "react";
import { BsCurrencyDollar, BsCart } from "react-icons/bs";
import { RiArrowDropDownLine } from "react-icons/ri";
import {
  FirstContainer,
  SecondContainer,
  ThirdContainer,
  Heading,
} from "../../styles/productsListing";
import {
  TopHeading,
  Items,
  Logo,
  Menu,
  SingleIcon,
  DropDownContent,
  DropItem,
} from "../../styles/topNav";
import ProductsListing from "../Products-Listing";

class TopNav extends React.Component {
  constructor(props: any) {
    super(props);
    this.state = {
      isOpen: false,
    };
  }
  openCategory = () => {
    this.setState({ isOpen: true });
  };

  render() {
    return (
      <>
        <TopHeading>
          <Menu>
            <Items>WOMEN</Items>
            <Items onClick={this.openCategory}>MEN</Items>
            <Items onClick={this.openCategory}>KIDS</Items>
          </Menu>

          <Logo>Logo</Logo>

          <div>
            <SingleIcon data-badge="2">
              <BsCart />
            </SingleIcon>

            <SingleIcon>
              <DropDownContent>
                <DropItem> $ USD </DropItem>
                <DropItem>&#8364; EUR </DropItem>
                <DropItem>&#165; JPY</DropItem>
              </DropDownContent>
            </SingleIcon>
          </div>
        </TopHeading>

        <FirstContainer>
          <Heading>Women</Heading>
        </FirstContainer>

        <SecondContainer>
          <Heading>Men</Heading>
        </SecondContainer>

        <ThirdContainer>
          <Heading>Children</Heading>
        </ThirdContainer>
      </>
    );
  }
}

export default TopNav;
